// src/components/Counter.js
import React from 'react';
import { useCounter } from '../store'; // Importe useCounter

function Counter() {
  const { count, increment, decrement } = useCounter(); // Use o hook useCounter

  return (
    <div>
      <h1>Counter App</h1>
      <p>Count: {count}</p>
      <button onClick={increment}>Increment</button>
      <button onClick={decrement}>Decrement</button>
    </div>
  );
}

export default Counter;
