  const { count, increment, decrement } = useCounter(); // Use o hook useCounter

  return (
    <html lang='pt-BR'>
      <head>
        <meta charSet='UFT-8'></meta>
        <meta name='viewport' content="width=device-width, initial-scale=1.0"></meta>
        <title>Exemplo de Input de Texto</title>

      </head>
      <body>
        <label for="nome"></label>
        <input type='text' id='nome' placeholder='digite algo'></input>
      </body>
    </html>
  );
}

export default Counter;
