// Store.js
import React, { useState } from 'react';
import './Store'; // Importe o arquivo de estilo CSS
import Categories from './Categories';
import ProductList from './ProductList';
import ShoppingCart from './ShoppingCart';


function Store() {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [cart, setCart] = useState([]);

  // Funções para adicionar e remover do carrinho

  return (
    <div>
      <h1>Minha Loja Online</h1>
      <Categories categories={categories} selectedCategory={selectedCategory} setSelectedCategory={setSelectedCategory} />
      <ProductList products={products} selectedCategory={selectedCategory} addToCart={addToCart} />
      <ShoppingCart cart={cart} removeFromCart={removeFromCart} />
    </div>
  );
}

export default Store;
